pragma solidity ^0.8.4;

import "../interfaces/IMintableERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CSTFaucet is Ownable {
  address _token;
  uint256 _claimAmount;
  mapping(address => bool) _claimed;
  
  event Claimed(address indexed, uint256);

  constructor(uint256 claimAmount_) {
    _claimAmount = claimAmount_;
  }
  
  function setToken(address token_) external onlyOwner {
    _token = token_;
  }

  function setClaimAmount(uint256 newAmount_) external onlyOwner {
    _claimAmount = newAmount_;
  }

  function hasClaimed(address claimee_) external view returns(bool) {
    return _claimed[claimee_];
  }

  function claim() external {
    require(!_claimed[msg.sender], 'already claimed!');
    _claimed[msg.sender] = true;
    IMintableERC20(_token).mint(msg.sender, _claimAmount);
    emit Claimed(msg.sender, _claimAmount);
  }
}
